import "core-js/modules/es.array.push.js";
export default {
  name: 'StaffNavbar',
  props: ['tabs', 'counter'],
  data: function () {
    return {
      inner_counter: this.counter
    };
  },
  methods: {
    newTab(tabname) {
      this.tabs.push({
        'name': tabname,
        'id': Date.now()
      });
      localStorage.setItem('tabs', JSON.stringify([...this.tabs]));
    }

  }
};
