import api from "@/api/auth"
import {setItem} from "@/helpers/persistanseStorage"
import { asyncIterator } from "core-js/fn/symbol"

const resource = api.resource

const state = {
    user: {},
    uid: null,
    isLoggedIn: false
}

export const mutationTypes = {
    loginStart: `[${resource}] loginStart`,
    loginSuccess: `[${resource}] loginSuccess`,
    loginFailure: `[${resource}] loginFailure`,
}

export const actionTypes = {
    login: `${resource} login`
}

const mutations = {
    [mutationTypes.loginSuccess](state, user) {
        state.user = user
      }
}

const actions = {
    async [actionTypes.login](context, data){
        return new Promise(resolve => {
            api.login(data).then()
            context.commit(mutationTypes.loginSuccess, data)
        })
    }
}

export default 
{
    state,
    mutations,
    actions
}
