    Send() {
      event.preventDefault();
      const pretoken = JSON.parse(localStorage.getItem("vuex"));
      const token = pretoken.auth.user.token; // document.getElementById('loading-page-lk').style.display = 'block'

      let admin = document.getElementById('admin').value.split(' ')[0];
      let staff = document.getElementById('staff').value.split(' ')[0];
      this.errors = {};

      if (this.answer1 == '') {
        this.errors = { ...this.errors,
          answer1: 'Выберите ответ'
        };
      }

      if (this.answer2 == '') {
        this.errors = { ...this.errors,
          answer2: 'Выберите ответ'
        };
      }

      if (this.answer3 == '') {
        this.errors = { ...this.errors,
