        };
      }

      if (this.emplyee == '') {
        this.errors = { ...this.errors,
          staff: 'Выберите ответ'
        };
      }

      if (!Object.keys(this.errors).length) {
        this.loader = true;
        fetch('http://10.1.5.65/api/reports/kpi/create/', {
          // fetch('/api/reports/kpi/create/', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json;charset=utf-8',
            'Authorization': `Basic ${token}`
          },
          body: JSON.stringify({
            "employee": staff,
            "manager": admin,
            "a1": this.answer1,
            "a2": this.answer2,
            "a3": this.answer3,
            "a4": this.answer4,
            "a5": this.answer5,
            "a6": this.answer6
          })
        }).then(response => {
          if (response.ok) {
            return response.json().then(data => {
              this.loader = false;
              this.notifyHead = 'Успешно';
              this.notifyMessage = 'Ваша анкета отправлена';
              this.notifyClass = 'wrapper-success';
              this.showNotify = true;
              setTimeout(this.closeNotification, 1500);
              document.getElementById("Anketa").reset();
            });
          } else {
            this.loader = false;
            this.notifyHead = 'Ошибка';
            this.notifyMessage = 'У вас нет прав доступа на предоставление';
            this.notifyClass = 'wrapper-alert';
            this.showNotify = true;
            setTimeout(this.closeNotification, 1500);
          }
        });
      } else {
        this.loader = false;
        this.notifyHead = 'Ошибка';
        this.notifyMessage = 'Заполните все поля анкеты';
        this.notifyClass = 'wrapper-alert';
        this.showNotify = true;
        setTimeout(this.closeNotification, 1500); // TODO: Нотификатор
      }
