      return this._settings.renderer.positionToCoordinate(this.getDataItemPositionY(dataItem, field, cellLocation, axisLocation));
    }
  });
  /**
   * @ignore
   */

  Object.defineProperty(DateAxis.prototype, "roundAxisPosition", {
    enumerable: false,
    configurable: true,
    writable: true,
    value: function (position, location) {
      var value = this.positionToValue(position);
      value = value - (location - 0.5) * this.baseDuration();
      var baseInterval = this.getPrivate("baseInterval");

      if (!$type.isNaN(value)) {
        var timeZone = this._root.timezone;
        var firstDayOfWeek = this._root.locale.firstDayOfWeek;
        value = $time.round(new Date(value), baseInterval.timeUnit, baseInterval.count, firstDayOfWeek, this._root.utc, new Date(this.getPrivate("min", 0)), timeZone).getTime();
        var duration = $time.getDateIntervalDuration(baseInterval, new Date(value), firstDayOfWeek, this._root.utc, this._root.timezone);

        if (timeZone) {
          value = $time.round(new Date(value + this.baseDuration() * 0.05), baseInterval.timeUnit, baseInterval.count, firstDayOfWeek, this._root.utc, new Date(this.getPrivate("min", 0)), timeZone).getTime();
          var newValue = value + duration * location;
          duration = $time.getDateIntervalDuration(baseInterval, new Date(newValue), firstDayOfWeek, this._root.utc, this._root.timezone);
