        // @todo check if is in range
        var dataItem = series.dataItems[result.index - 1];

        if (dataItem) {
          if (dataItem.open && dataItem.close) {
            var open_2 = dataItem.open[fieldName];
            var close_2 = dataItem.close[fieldName];

