            tooltip.set("bounds", {
              left: plotT.x,
              top: plotT.y,
              right: plotB.x,
              bottom: prevY_1
            });
            tooltip.setPrivate("customData", {
              left: plotT.x,
              top: plotT.y,
              right: plotB.x,
              bottom: prevY_1
            });
            prevY_1 = Math.min(prevY_1 - height, tooltip._fy - height);

            if (tooltip.parent == tooltipContainer_1) {
