            var prevBottom_1 = prevY_1;
            $array.each(tooltips, function (tooltip) {
              var bounds = tooltip.get("bounds");

              if (bounds) {
                var top_1 = bounds.top - prevY_1;
                var bottom = bounds.bottom - prevY_1;

                if (top_1 < prevBottom_1) {
                  top_1 = prevBottom_1;
                  bottom = top_1 + tooltip.height();
                }

