import PersonnelServiceNavbar from '../components/Navbar/PersonnelServiceNavbar.vue';
export default {
  name: 'PersonnelService',
  components: [PersonnelServiceNavbar],

  data() {
    return {
      tabs: [],
      counter: 1
    };
  },

  methods: {
    closeTab(x) {
      for (let i = 0; i < this.tabs.length; i++) {
        if (this.tabs[i].id === x) {
          this.tabs.splice(i, 1);
        }
      }

      localStorage.setItem('tabs', JSON.stringify([...this.tabs]));
    }

  },

  mounted() {
    const tabs = localStorage.getItem('tabs');

    if (tabs) {
      this.tabs = JSON.parse(tabs);
    }
  }

};
