import {api} from '@/helpers/axios'

export const resource = 'personal'

const getAllStaff = () => {
   return api.get(`${resource}/users/`)
}
const deleteUser = (id) => {
    return api.delete(`${resource}/users/delete/` + `${id}`)
}
const currentUser = (id) => {
    return api.get(`${resource}/users/` + `${id}`)
}

export default {
    getAllStaff,
    deleteUser
}
